package fs.java.basic;

public class Program2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0 ,j=0;
		System.out.println("Printing name horizontally");
		System.out.println("---------------------------------------");
		while(i < 10) {
			System.out.print("Wipro"+"\t");
			i++;
		}
		System.out.println("\n\n" + "Printing name vertically");
		System.out.println("---------------------------------------");
		while(j < 10) {
			System.out.println("Wipro");
			j++;
		}
	}

}
